import React, { useState } from "react";
import { motion } from "framer-motion";

const users = [
  { id: 1, name: "Alice", age: 23, bio: "Loves hiking" },
  { id: 2, name: "Bob", age: 28, bio: "Coffee enthusiast" },
  { id: 3, name: "Charlie", age: 26, bio: "Musician" },
];

const SwipeCards = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleSwipe = (direction) => {
    setCurrentIndex((prev) => (prev + 1) % users.length);
  };

  return (
    <div>
      <h2>Discover</h2>
      {users[currentIndex] && (
        <motion.div
          key={users[currentIndex].id}
          drag="x"
          dragConstraints={{ left: 0, right: 0 }}
          dragElastic={0.2}
          onDragEnd={(e, info) => {
            if (info.point.x > 100) handleSwipe("right");
            else if (info.point.x < -100) handleSwipe("left");
          }}
          style={{
            border: "1px solid gray",
            borderRadius: "10px",
            padding: "20px",
            margin: "20px",
            maxWidth: "300px",
          }}
        >
          <h3>{users[currentIndex].name}, {users[currentIndex].age}</h3>
          <p>{users[currentIndex].bio}</p>
        </motion.div>
      )}
    </div>
  );
};

export default SwipeCards;
