import React, { useState } from "react";
import "./Chat.css";

const matches = [
  { id: 1, name: "Alice", image: "https://randomuser.me/api/portraits/women/1.jpg" },
  { id: 2, name: "Bob", image: "https://randomuser.me/api/portraits/men/2.jpg" },
  { id: 3, name: "Clara", image: "https://randomuser.me/api/portraits/women/3.jpg" },
];

const Chat = () => {
  const [selectedUser, setSelectedUser] = useState(matches[0]);
  const [messages, setMessages] = useState([
    { sender: "them", text: "Hey! How are you?" },
    { sender: "me", text: "Hi! I'm good, you?" },
  ]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (input.trim() !== "") {
      setMessages([...messages, { sender: "me", text: input }]);
      setInput("");

      // Simulate a reply
      setTimeout(() => {
        setMessages(prev => [...prev, { sender: "them", text: "Cool! ðŸ˜„" }]);
      }, 800);
    }
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <h3>Matches</h3>
        {matches.map(user => (
          <div
            key={user.id}
            className={`match ${selectedUser.id === user.id ? "active" : ""}`}
            onClick={() => setSelectedUser(user)}
          >
            <img src={user.image} alt={user.name} />
            <span>{user.name}</span>
          </div>
        ))}
      </div>

      <div className="chat-box">
        <div className="chat-header">
          <img src={selectedUser.image} alt={selectedUser.name} />
          <h3>{selectedUser.name}</h3>
        </div>

        <div className="messages">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`message ${msg.sender === "me" ? "me" : "them"}`}
            >
              {msg.text}
            </div>
          ))}
        </div>

        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
